import { validateProps } from "../../common/types";

export default function validate(values: validateProps) {
  let errors = {} as validateProps;

  if (!values.voornaam) {
    errors.voornaam = "Voornaam is verplicht";
  }
  if (!values.achternaam) {
    errors.achternaam = "Achternaam is verplicht";
  }
  if ((values.email) && (!/\S+@\S+\.\S+/.test(values.email))) {
    errors.email = "Email adres is onjuist";
  } 
  if (!values.straat) {
    errors.straat = "Straatnaam is verplicht";
  }
  if (!values.huisnummer) {
    errors.huisnummer = "Huisnummer is verplicht";  
  }
  if (!values.postcode) {
    errors.postcode = "Postcode is verplicht";
  }
  if (!values.plaats) {
    errors.plaats = "Woonplaats is verplicht";
  }
  
  if (!values.message) {
    errors.message = "Bericht is verplicht";
  }

  
  
  return errors;
}
