import IntroContent from "../../content/IntroContent.json";
import MiddleBlockContent from "../../content/MiddleBlockContent.json";
import AboutContent from "../../content/AboutContent.json";
import MissionContent from "../../content/MissionContent.json";
import ProductContent from "../../content/ProductContent.json";
import ContactContent from "../../content/ContactContent.json";
import { useEffect } from "react";
import React, { useState, lazy } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
  Link,
  useHistory
} from "react-router-dom";
import "./styles.css";



const Contact = lazy(() => import("../../components/ContactForm"));
const MiddleBlock = lazy(() => import("../../components/MiddleBlock"));
const Container = lazy(() => import("../../common/Container"));
const ScrollToTop = lazy(() => import("../../common/ScrollToTop"));
const ContentBlock = lazy(() => import("../../components/ContentBlock"));

export default function App() {
  const [user, setUser] = useState(false);
  return (
    <Router className="App">
      <User status={user}></User>

      <ul>
        <li>
          <Link to="/index">Index</Link>
        </li>
        <li>
          <Link to="/protected">Protected Page</Link>
        </li>
      </ul>

      <Switch>
        <Route path="/index">
          <Index></Index>
        </Route>
        <Route path="/protected">
          <Protected status={user}></Protected>
        </Route>
        <Route path="/login">
          <Login auth={setUser}></Login>
        </Route>
      </Switch>
    </Router>
  );
}

function User({ status }) {
  return status ? <h1>Hello user!</h1> : <h1>Please log in</h1>;
}

function Index() {
  return (
    <div>
      <h2>Index page</h2>
      <p>All users can see this</p>
    </div>
  );
}

function Protected({ status }) {
  return status ? (
    <div><h2>Protected page</h2><p>You are logged in and can see this page.</p></div>
  ) : (
    <Redirect to="/login" />
  );
}

function Login({ auth }) {
  let history = useHistory();
  return (
    <div>
      <h2>LogIn page</h2>
      <p>You can log in by pressing the button below</p>
      <button
        onClick={() => {
          auth(true);
          history.push("/protected");
        }}
      >
        Log In
      </button>
    </div>
  );
}
