import { validateProps } from "../../common/types";

export default function validate(values: validateProps) {
  let errors = {} as validateProps;

  if (!values.naam) {
    errors.naam = "Naam is verplicht";
  }
  if (!values.email) {
    errors.email = "Email adres is verplicht";
  } else if (!/\S+@\S+\.\S+/.test(values.email)) {
    errors.email = "Email adres is ongeldig";
  }
  if (!values.straat) {
    errors.straat = "Straatnaam is verplicht";
  }
  if (!values.huisnummer) {
    errors.huisnummer = "Huisnummer is verplicht";  
  }
  if (!values.plaats) {
    errors.plaats = "Postcode is verplicht";
  }
  if (!values.pl) {
    errors.postalcode = "Postcode is verplicht";
  }
  
  if (!values.message) {
    errors.message = "Bericht is verplicht";
  }
  if (!values.city) {
    errors.city = "Woonplaats is verplicht";
  }
  
  
  return errors;
}
