import styled from "styled-components";

export const OtpGroup = styled("div")`
  display: flex;
  width: 100%;
  max-width: 360px;
  column-gap: 10px;

`;

export const OtpInput = styled("input")`
  width: 100%;
  height: 60px;
  border: 1px solid #ccc;
  border-radius: 5px;
  text-align: center;
  font-size: 16px;
  font-weight: bold;
  line-height: 1;
`;
