import { OtpGroup, OtpInput } from './styles.';
import { Container, TextWrapper, Content } from "./styles";

export type Props = {
  value: string;
  valueLength: number;
  onChange: (value: string) => void;
};

export default function LoginBlock({ value, valueLength, onChange }: Props) {
  return (
    <OtpGroup>
      {[1, 2, 3, 4, 5, 6].map((digit, idx) => (
        <OtpInput
          key={idx}
          type="text"
          inputMode="numeric"
          autoComplete="one-time-code"
          pattern="\d{1}"
          maxLength={valueLength}
          className="otp-input"
          value={digit}
        />
      ))}
    </OtpGroup>
  );
}
